import React, { useEffect, useState } from 'react';
import './App.css';
import Dice from './Dice';

const App = () => {

  const [dices, setDices] = useState([]);

  useEffect(() => {
    setDices(createNewDice());
  }, []);

  const createNewDice = () => {
    let newDiceArray = [];
    for (let i = 0; i < 10; i++) {
      const dice = {
        id: i + 1,
        value: Math.ceil(Math.random() * 6),    // if are not expecting 0 use {ceil}
        held: false
      } 
      newDiceArray.push(dice)
    }
    return newDiceArray;
  }

    const listDices = dices.map(dice => <Dice key={dice.id} value={dice.value} />)

    return (
      <main>
        <h1>Tenzies</h1>
        <p>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
        <div className='diceContainer'>
          {listDices}
        </div>
        < button onClick={createNewDice}>Press</button>
      </main>
    );
  }

  export default App;
