import React, { useEffect, useState } from 'react';
import './App.css';
import Dice from './Dice';

const App = () => {

  const [dice, setDice] = useState([]);

  useEffect(() => {
    const Array = [];
    for (let i = 0; i < 10; i++) {
      const obj = { id: i }
      Array.push(obj)
    }
    setDice(Array);
  }, []);

  const newDice = () => {
    let newDiceArray = [];
    for (let i = 0; i < 10; i++) {
      const dice = {
        id: i + 1,
        value: Math.ceil(Math.random() * 6)    // if are not expecting 0 use {ceil}
      }
      let num = Math.ceil(Math.random() * 6)  
      newDiceArray.push(num)
    }
    return newDiceArray;
  }

    const list = dice.map(object => <Dice key={object.id} value={object.id} />)

    return (
      <main>
        <h1>Tenzies</h1>
        <p>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
        <div className='diceContainer'>
          {list}
        </div>
        < button onClick={newDice}>Press</button>
      </main>
    );
  }

  export default App;
